import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Auth.css';

function Registro() {
  const navigate = useNavigate();

  // Estado del formulario y otros estados necesarios
  const [formData, setFormData] = useState({
    tipousuario: '',
    nombre: '',
    apellido_paterno: '',
    apellido_materno: '',
    dni: '',
    correo: '',
    contrasena: ''
  });
  const [estudiantes, setEstudiantes] = useState([]);
  const [selectedEstudiante, setSelectedEstudiante] = useState('');
  const [dniError, setDniError] = useState('');

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    const { name, value } = e.target;

    // Validación específica para el campo DNI
    if (name === 'dni') {
      // Permitir solo números
      if (/^\d*$/.test(value)) {
        setFormData({
          ...formData,
          [name]: value
        });
        
        // Validar longitud del DNI
        if (value.length === 8) {
          setDniError(''); // Limpiar error si cumple con la longitud
        } else {
          setDniError('Coloque un DNI válido de 8 dígitos');
        }
      }
    } else {
      // Para otros campos
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  // Manejar el cambio del tipo de usuario
  const handleTipoUsuarioChange = (e) => {
    setFormData({
      ...formData,
      tipousuario: e.target.value,
    });

    // Si el tipo de usuario es 'Padre', cargar la lista de estudiantes
    if (e.target.value === 'Padre') {
      axios.get(`${process.env.REACT_APP_API_URL}/api/get_estudiantes.php`)
        .then(response => {
          console.log(response.data);
          setEstudiantes(response.data);
        })
        .catch(error => {
          console.error('Error al obtener los estudiantes:', error);
        });
    }
  };

  // Manejar el cambio del estudiante seleccionado
  const handleEstudianteChange = (e) => {
    setSelectedEstudiante(e.target.value);
  };

  // Manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validación final del DNI antes de enviar
    if (formData.dni.length !== 8) {
      setDniError('Coloque un DNI válido de 8 dígitos');
      return;
    }

    const dataToSend = {
      ...formData,
      id_estudiante: formData.tipousuario === 'Padre' ? selectedEstudiante : null
    };

    console.log("Datos que se envían al backend:", dataToSend);

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/registro.php`, dataToSend);
      console.log('Usuario registrado:', response.data);
      alert('¡Registro exitoso! Serás redirigido a la página de inicio.');
      navigate('/');
    } catch (error) {
      console.error('Error al registrar el usuario:', error.response?.data || error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Registro</h2>

      <label>
        Nombre:
        <input type="text" name="nombre" value={formData.nombre} onChange={handleChange} required />
      </label>

      <label>
        Apellido Paterno:
        <input type="text" name="apellido_paterno" value={formData.apellido_paterno} onChange={handleChange} required />
      </label>

      <label>
        Apellido Materno:
        <input type="text" name="apellido_materno" value={formData.apellido_materno} onChange={handleChange} />
      </label>

      <label>
        DNI:
        <input type="text" name="dni" value={formData.dni} onChange={handleChange} maxLength="8" required />
        {dniError && <p className="error-message">{dniError}</p>}
      </label>

      <label>
        Correo:
        <input type="email" name="correo" value={formData.correo} onChange={handleChange} required />
      </label>

      <label>
        Contraseña:
        <input type="password" name="contrasena" value={formData.contrasena} onChange={handleChange} required />
      </label>

      <label>
        Tipo de Usuario:
        <select name="tipousuario" value={formData.tipousuario} onChange={handleTipoUsuarioChange} required>
          <option value="">Seleccionar</option>
          <option value="Docente">Docente</option>
          <option value="Estudiante">Estudiante</option>
          <option value="Padre">Padre o Apoderado</option>
        </select>
      </label>

      {/* Mostrar el selector de estudiantes solo si el tipo de usuario es 'Padre' */}
      {formData.tipousuario === 'Padre' && (
        <label>
          Estudiante Relacionado:
          <select value={selectedEstudiante} onChange={handleEstudianteChange} required>
            <option value="">Seleccionar Estudiante</option>
            {estudiantes.map(estudiante => (
              <option key={estudiante.id} value={estudiante.id}>
                {estudiante.nombreCompleto || 'Nombre no disponible'}
              </option>
            ))}
          </select>
        </label>
      )}

      <button type="submit">Registrarse</button>
    </form>
  );
}

export default Registro;
