import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // Importa useNavigate
import './Auth.css';  // Asegúrate de tener el archivo CSS

function Registro() {
  const navigate = useNavigate();  // Inicializa useNavigate para redirigir

  // Estado del formulario
  const [formData, setFormData] = useState({
    tipousuario: '',
    nombre: '',
    apellido_paterno: '',
    apellido_materno: '',
    dni: '',
    correo: '',
    contrasena: ''
  });

  const [estudiantes, setEstudiantes] = useState([]); // Estado para la lista de estudiantes
  const [selectedEstudiante, setSelectedEstudiante] = useState(''); // Estado para el estudiante seleccionado

  // Manejar cambios en el formulario
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Manejar el cambio del tipo de usuario
  const handleTipoUsuarioChange = (e) => {
    setFormData({
      ...formData,
      tipousuario: e.target.value,
    });

    // Si el tipo de usuario es 'Padre', cargamos la lista de estudiantes
    if (e.target.value === 'Padre') {
      axios.get(`${process.env.REACT_APP_API_URL}/api/get_estudiantes.php`) // Aquí haces la llamada al backend para obtener los estudiantes
        .then(response => {
          console.log(response.data);  // Verifica los datos en la consola
          setEstudiantes(response.data);  // Cargar estudiantes al estado
        })
        .catch(error => {
          console.error('Error al obtener los estudiantes:', error);
        });
    }
  };

  // Manejar el cambio del estudiante seleccionado
  const handleEstudianteChange = (e) => {
    setSelectedEstudiante(e.target.value);
  };

  // Manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    const dataToSend = {
      ...formData,
      id_estudiante: formData.tipousuario === 'Padre' ? selectedEstudiante : null  // Solo enviar id_estudiante si es 'Padre'
    };

    console.log("Datos que se envían al backend:", dataToSend);  // Verifica los datos que se envían

    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/registro.php`, dataToSend);
      console.log('Usuario registrado:', response.data);
      // Manejar redirección o mensaje de éxito aquí
      // Redirigir a la página de inicio (o la que prefieras) después del registro exitoso
      alert('¡Registro exitoso! Serás redirigido a la página de inicio.');
      navigate('/');  // Aquí lo rediriges a la página de inicio. Puedes cambiar '/' por la ruta que desees.
    } catch (error) {
      console.error('Error al registrar el usuario:', error.response?.data || error.message);  // Verifica el error
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Registro</h2>

      <label>
        Nombre:
        <input type="text" name="nombre" value={formData.nombre} onChange={handleChange} required />
      </label>

      <label>
        Apellido Paterno:
        <input type="text" name="apellido_paterno" value={formData.apellido_paterno} onChange={handleChange} required />
      </label>

      <label>
        Apellido Materno:
        <input type="text" name="apellido_materno" value={formData.apellido_materno} onChange={handleChange} />
      </label>

      <label>
        DNI:
        <input type="text" name="dni" value={formData.dni} onChange={handleChange} required />
      </label>

      <label>
        Correo:
        <input type="email" name="correo" value={formData.correo} onChange={handleChange} required />
      </label>

      <label>
        Contraseña:
        <input type="password" name="contrasena" value={formData.contrasena} onChange={handleChange} required />
      </label>

      <label>
        Tipo de Usuario:
        <select name="tipousuario" value={formData.tipousuario} onChange={handleTipoUsuarioChange} required>
          <option value="">Seleccionar</option>
          <option value="Docente">Docente</option>
          <option value="Estudiante">Estudiante</option>
          <option value="Padre">Padre o Apoderado</option>
        </select>
      </label>

      {/* Mostrar el selector de estudiantes solo si el tipo de usuario es 'Padre' */}
      {formData.tipousuario === 'Padre' && (
        <label>
          Estudiante Relacionado:
          <select value={selectedEstudiante} onChange={handleEstudianteChange} required>
            <option value="">Seleccionar Estudiante</option>
            {estudiantes.map(estudiante => (
              <option key={estudiante.id} value={estudiante.id}>
                {estudiante.nombreCompleto || 'Nombre no disponible'} {/* Mostrar algo por defecto si nombreCompleto es null */}
              </option>
            ))}
          </select>
        </label>
      )}

      <button type="submit">Registrarse</button>
    </form>
  );
}

export default Registro;
